import React, { Component } from "react";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import { DialogContent } from "@material-ui/core";


const SimpleDialog = (props) =>{
	const {onClose , open} = props;

	const handleClose = ()=>{
		onClose();
	}

	return(
		<Dialog onClose={handleClose} aria-labelledby="simple-dialog-title" open={open}>
			<DialogTitle id="simple-dialog-title">New Payment</DialogTitle>
			<DialogContent>
				<form style = {{ display : "flex", flexWrap : "wrap", }}>
					<FormControl style = {{ spacing : "2", minWidth : 200,}}>
						<TextField
							autoFocus
							margin="dense"
							id="name"
							label="Amount"
							type="email"
							fullWidth
						/>
					</FormControl>

					<FormControl style = {{ spacing : "2", minWidth : 200,}}>
						<InputLabel htmlFor="demo-dialog-native">Paid by</InputLabel>
						<Select native>
							<option aria-label="None" value="" />
							<option value="John Wick">John Wick</option>
							<option value="Satya">Satya</option>
							<option value="Marilyn Manson">Marilyn Manson</option>
						</Select>
					</FormControl>

					<FormControl style = {{ spacing : "2", minWidth : 400,}}>
						<TextField
							autoFocus
							margin="dense"
							id="name"
							label="Amount"
							type="email"
							fullWidth
						/>
					</FormControl>

				</form>
			</DialogContent>
		</Dialog>
	);
}

class AddPaymentForm extends Component{
	state = {
		open : false,
	}

	handleSubmit = (e) =>{

	}

	handleChange = (e) =>{

	}

	handleClose = () =>{
		this.setState({
			open : false,
		});
	}

	handleClickOpen = () =>{
		this.setState({
			open : true,
		});
	}

	render(){
		return(
		   <div className = "paymentForm">
				<Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
					Add New Payment
				</Button>
      			<SimpleDialog open={this.state.open} onClose={this.handleClose} />
		   </div>
		);
	}
}

export default AddPaymentForm;
