import React, { Component } from "react";
import Button from '@material-ui/core/Button';
import PaymentInfoDialog from "./PaymentInfoDialog";
import Payment from "../utils/Payment";

class AddPaymentForm extends Component{
	state = {
		open : false,
		paymentList : [],
	}

	handleClose = ({amount, paidAt, paidBy}) =>{
		if(amount && paidAt && paidBy)
			return;

		let ob = new Payment(amount, paidAt, paidBy);
		this.setState({
			open : false,
			paymentList : [...(this.paymentList), ob],
		});
	}

	handleClickOpen = () =>{
		this.setState({
			open : true,
		});
	}

	render(){
		return(
		   <div className = "paymentForm">
				<Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
					Add New Payment
				</Button>
      			<PaymentInfoDialog open={this.state.open} onClose={this.handleClose} />
		   </div>
		);
	}
}

export default AddPaymentForm;
