import React, { Component } from "react";

// utils
import Person from "../utils/Person";

// Material ui componenets
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class AddPerson extends Component{
	state = {
		name : null,
	}

	handleSubmit = ()=>{
		if(this.state.name){
			let x = new Person(this.state.name, this.props.numberOfPeople + 1);
			(this.props.addPerson)(x);
		}
	}

	handleChange = (e)=>{
		this.setState({
			name : e.target.value,
		})
	}

    render(){
		return(
			<div className = "addPerson">
				<form>
					<FormControl style = {{ spacing : "2", minWidth : 400,}}>
						<TextField
							autoFocus
							margin="dense"
							id="name"
							label="Name"
							type="name"
							fullWidth
							onChange = {this.handleChange}
						/>
					</FormControl>

					<Button autoFocus variant = "contained" onClick={this.handleClose} color="primary"
							style = {{ marginTop : 12 }} disableElevation>
						Add
					</Button>
				</form>


			</div>
		);
	}
}

export default AddPerson;
